export const usePlanetContract = (web3: Web3 | null) => {
	if (!web3) {
		return {
			contractAddress: CONTRACT_ADDRESS,
			mintPlanet: (options: SendOptions) => {
				throw new Error("wallet not installed");
			},
			tokenURI: (tokenId: string) => {
				throw new Error("wallet not installed");
			},
			ownerOf: (tokenId: string) => {
				throw new Error("wallet not installed");
			},
			totalSupply: () => {
				throw new Error("wallet not installed");
			},
		};
	}
	const contract = new web3.eth.Contract(
		contractAbi as any,
		CONTRACT_ADDRESS
	);

	return {
		contractAddress: CONTRACT_ADDRESS,
		mintPlanet: (options: SendOptions) =>
			contract.methods.mintPlanet().send(options),
		tokenURI: (tokenId: string) =>
			contract.methods.tokenURI(tokenId).call(),
		ownerOf: (tokenId: string) => contract.methods.ownerOf(tokenId).call(),
		totalSupply: () => contract.methods.totalSupply().call(),
	};
};
